import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";

// Leaflet requires fixing marker icons separately
import L from "leaflet";
import markerIconPng from "leaflet/dist/images/marker-icon.png";
import markerShadowPng from "leaflet/dist/images/marker-shadow.png";

const ShelterMap = () => {
    const [shelters, setShelters] = useState([]);

    useEffect(() => {
        /*const fetchShelters = async () => {
            try {
                let response = await fetch(
                    'https://overpass-api.de/api/interpreter?data=[out:json];node["amenity"="shelter"](37.0,-120.0,39.0,-118.0);out body;'
                );
                let data = await response.json();
                setShelters(data.elements);
            } catch (error) {
                console.error("Error fetching shelter data:", error);
            }
        };*/

        fetchShelters();
    }, []);

    const customIcon = new L.Icon({
        iconUrl: markerIconPng,
        shadowUrl: markerShadowPng,
        iconSize: [25, 41],
        iconAnchor: [12, 41],
    });

    return (
        <MapContainer 
        center={[37.5, -119.5]} 
        zoom={6} 
        style={{ height: "500px", width: "100%" }}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            {shelters.map((shelter) => (
                <Marker
                    key={shelter.id}
                    position={[shelter.lat, shelter.lon]}
                    icon={customIcon}
                >
                    <Popup>
                        <strong>{shelter.tags.name || "Unnamed Shelter"}</strong>
                    </Popup>
                </Marker>
            ))}
        </MapContainer>
    );
};

export default ShelterMap;
